    private void checkAccountButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkAccountButtonActionPerformed
        User.curUser = new SqlHandler();
        if(User.curUser.matchAccNumber(new String(accNoField.getText())))
        {
            if(User.curUser.lockState()
                    )
            {
                loginAccountDialog.dispose();
                lockedNotifDialog.setVisible(true);
              
            }
            else
            {
            loginAccountDialog.dispose();
            this.dispose();
            new VerifyPwdFrame().setVisible(true);
            }
        }
        else
        {
            accNoField.setText("");
            notFoundDialog.setVisible(true);
        }
    }//GEN-LAST:event_checkAccountButtonActionPerformed

