    private void checkAccountForgotButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkAccountForgotButtonActionPerformed
         User.curUser = new SqlHandler();
        if(User.curUser.matchAccNumber(new String(accNoForgotField.getText())))
        {
           forgotAccountVerifyDialog.dispose();
           new ForgotFrame().setVisible(true);
        }
        else
        {
            notFoundDialog.setVisible(true);
        }
    }//GEN-LAST:event_checkAccountForgotButtonActionPerformed

