    private void checkPinButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkPinButtonActionPerformed
     
     if(User.curUser.attempts==0)
     {
         this.setVisible(false);
         exceedMaxAttemptDialog.setVisible(true);
     }
    else if(User.curUser.matchPin(User.encodePin(new String(pinField.getPassword()))))
      {
          if(User.curUser.attempts!=3)
                User.curUser.setAttempts(User.MAXATTEMPTS);
          
          this.dispose();
          new TransactionFrame().setVisible(true);
      }
     else
      {
          User.curUser.setAttempts(User.curUser.attempts-1);
          pwdIncorrectDialog.setVisible(true);
      }
      
    }//GEN-LAST:event_checkPinButtonActionPerformed

